<?php/*--------------------------------------------*//* Email Submited Appointment Form                *//*--------------------------------------------*/$post = (!empty($_POST)) ? true : false;if($post){    $to = "your_email@your_domain.com"; // Your e-mail here	$firstName = stripslashes($_POST['inputFirstName']);	$middleName = stripslashes($_POST['inputMiddleName']);	$lastName = stripslashes($_POST['inputLastName']);	$gender = stripslashes($_POST['inputGender']);	$address = stripslashes($_POST['inputAddress']);	$city = stripslashes($_POST['inputCity']);	$phone = stripslashes($_POST['inputPhone']);	$insurance = stripslashes($_POST['haveInsurance']);	$reason = stripslashes($_POST['inputReason']);	$date = stripslashes($_POST['inputDate']);	$time = stripslashes($_POST['desirableTime']);	$headers = "From: ".$gender." ".$firstName." ".$middleName." ".$lastName."\r\n"             ."X-Mailer: PHP/" . phpversion();	$subject = "I would like to make an appointtment for date: ".$date." time: ".$time."\r\n";    $message = "Dear Sirs,"."\r\n"             ."I would like to make an appointtment for date: ".$date." time: ".$time."\r\n"             ."I ".$insurance."\r\n"             ."Reason for visit: ".$reason."\r\n"             ."Address: ".$address." ".$city."Phone: ".$phone; 			 		$mail = mail($to, $headers, $subject, $message);	if($mail){		echo 'OK';	}}?>